insert into public.quest (level, title, description, duration, fame, tribute, experience) values (3, 'Охота на ведьмочек', 'Три миловидные ведьмы проводят в нашем сарае свои глупые ритуалы, а как нам картоху копать без лопат? Мешочек на :tribute монет ждёт героев.', 10, 30, 200, 300);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (2, 'Гоблинский шабаш', 'Орава гоблинов совсем распоясалась, нормальным людям спать не даёт, гоните их в шею, а с меня :tribute монет.', 10, 20, 350, 200);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (1, 'Бить и пить', 'Дам бочку отличного пива и мешочек со :tribute монетами тому кто прогонит с моей винодельни проклятых гарпий.', 10, 10, 150, 100);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (5, 'Хочу рыбачить', 'Прибейте кто-нибудь этих утопцев наконец, воду мутят, рыбу жрут, рыбачить невозможно. Готов отдать :tribute монет тому кто избавится от негодных.', 10, 50, 570, 500);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (10, 'Голем', 'Я - Великий хранитель тайн и запретных знаний Астох создал невероятное создание - настоящего мышиного Голема. Так теперь эта сволочь сидит в подвале и пожирает мои харчи. Удавите его поскорее и вынесите куда-нибудь в лес. Сокровища в :tribute монет ждут смельчаков.', 10, 100, 890, 1000);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (5, 'Ради науки', 'Мне нужны помет летучих мышей Кролоса один пуд, хвосты игуан Гарула штук 10 и ещё 12 голов кусь-рыбы с Тароса. Знание алхимии приветствуется (те кто не знает где у кусь-рыбы хвост а где башка даже не приходите). Даю :tribute монет.', 22, 50, 1050, 500);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (7, 'Веселые тролли', 'Приходите на праздник в наш город, у нас будут тролли! Они будут плясать, петь песни, устраивать представления. Научат жить не зная зла и бед! Вход бесплатый!', 210, 50, 0, 600);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (4, 'Наглые гарпии', 'Кучка гарпий поселилась на наших полях! Чего им там нужно, это же не утесы! Пугал ставили, так они из них гнёзда устроили. Короче гоните пернатых, мы для этих целей :tribute монетами скинулись.', 305, 40, 250, 260);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (8, 'Ходячий валежник', 'Тут в лесу завелись какие-то живые кусты, когда бабы за валежником ходили - одного такого чуть на дрова не порубили, он их поптом до самой деревни гнал. Шаман говорит что это дендрарианы или как-то так. Вобщем отчистите лес от наваждения, награда :tribute монет.', 1150, 90, 950, 1020);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (1, 'Крысы в подвале', 'В моем подвале завелись крысы! Шуршат всю ночь что не уснуть. Запасы подъедают, и ладно бы зерно, так они буженину предпочитают! Зачистите подвал от этих тварей, оплачиваю по хвостам, до :tribute монет.', 150, 15, 80, 220);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (6, 'Зайцы-оборотни', 'Здесь довечи странный зверь завёлся - днём обычный заяц, а как тьма наступит в зубастое чудище превращается! Наши охотники поймали пару таких в силки, домой принесли, а ночью им хаты разнесло. Помогите с напастью и :tribute монет ваши.', 750, 75, 850, 780);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (12, 'Полуночницы', 'Ищутся герои которые помогут с бедствием постигнувшим нашу деревню. В заброшеном доме поселились полуночницы - днем от них проблем нет, а вот ночью вылезают из дому и ходят стучаться к соседям - кто открыл тот пропал! Так вся деревня скоро вымрет, приходите мы собрали :tribute монет.', 1250, 120, 1540, 1450);
insert into public.quest (level, title, description, duration, fame, tribute, experience) values (15, 'Жуткий замок', 'У нас рядом есть старый замок, и вот неавно там всякая нечисть завелась. Земля вокруг замка стала непладородной, по ночам призраки гуляют вокруг, говорят зомби видели. А ещё говорят тёмный лорд там завелся, ещё чего решит отправится мир завоёвывать а оно нам надо? Вобщем с города :tribute монет, с героев разогнать нежить!', 2540, 200, 2400, 2500);
